package com.iffy.mianshi.algorithm.leecode;

public class stringOrder567 {
    public static void main(String[] args) {

       String a =  "";
       String b = "";

       Boolean result =  checkInclusion(a,b);
       System.out.println(result);

    }
    public static boolean checkInclusion(String s1, String s2) {
        //一字符串的全排列 然后检查 s2.contain

        //二滑动窗口法
        if(s2.length()<s1.length()){
            return false;
        }
        for(int i=0;i<=s2.length()-s1.length();i++){
            //System.out.println(i);
            //System.out.println(s2.substring(i,i+s1.length()));
            if(checkString(s1,s2.substring(i,i+s1.length()))){
                return true;
            }
        }
        return false;


    }
    private static boolean checkString(String s1, String s2){
        for(int i =0;i<s2.length();i++){
            if(s1.contains(s2.charAt(i)+"")){
                s1= s1.replaceFirst(s2.charAt(i)+"","");
            }else{
                return false;
            }
        }
        if("".equals(s1)){
            return true;
        }else{
            return false;
        }

    }

}

